    if(radios.length > 1){
      console.log(radios,radios[0].name);
      var length = radios.length;
      var prev=radios[0].name;
      var current = radios[1].name;
      count=1;
      for(var i = 1; i < length; i++ ){
        current = radios[i].name;
        if(prev != current){
          count++;
        }
        prev = current;
      }
    }
